import React from 'react';
import './index.css';
import ChatWindow from './ChatWindow';
import ChatForm from './ChatForm';
import Footer from './Footer';
import Logo from './Logo';

class ChatPage extends React.Component {
    state = {
        input: "",
        messages: []
    };

    handleInput = event => {
        this.setState({ input: event.target.value });
    }

    handleSubmit = async event => {
        event.preventDefault();
        const message = { message: this.state.input };
        this.setState(prevState => ({ messages: [...prevState.messages, message], input: "" }));

        try {
            const response = await fetch("http://localhost:8000/chat/", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(message)
            });
            const data = await response.json();
            this.setState(prevState => ({ messages: [...prevState.messages, { message: data.message, fromBot: true }] }));
        } catch (error) {
            console.error("An error occurred:", error);
        }
    }

    render() {
        return (
            <>
                <div className="App min-h-screen flex flex-col justify-between pt-6 w-full pr-4 sm:pr-8 pb-6" style={{ background: '#111', backgroundImage: `url(${process.env.PUBLIC_URL}/smoky_background.jpg)`, backgroundPosition: 'left', backgroundRepeat: 'no-repeat', backgroundSize: 'cover' }}>
                    <div className="flex flex-col justify-center items-end px-2 sm:px-4 md:px-0">
                        <Logo />
                        <header className="App-header w-full sm:w-4/5 md:w-3/4 lg:w-2/3 xl:w-1/2 2xl:w-1/3 bg-gray-800 text-gray-200 rounded-lg shadow-2xl overflow-hidden mt-4 sm:mt-6 md:mt-11">
                            <h1 className="text-xl sm:text-2xl p-6" style={{ color: '#b08517', fontFamily: 'PeakyBlinderFont, cursive', textShadow: '1px 1px 2px black, 0 0 1em gold, 0 0 0.2em gold' }}>Ask me anything peaky !!</h1>
                            <ChatWindow messages={this.state.messages} />
                            <ChatForm input={this.state.input} handleInput={this.handleInput} handleSubmit={this.handleSubmit} />
                        </header>
                    </div>
                </div>
                <Footer />
            </>
        );
    }
}

export default ChatPage;
