import React from 'react';
import './index.css';
import ChatWindow from './ChatWindow';
import ChatForm from './ChatForm';
import Footer from './Footer';

class ChatPage extends React.Component {
    // ... rest of your code
    render() {
        return (
            <div className="App min-h-screen flex flex-col justify-center items-end pt-6 w-full pr-8 pb-6" style={{ background: '#111', backgroundImage: `url(${process.env.PUBLIC_URL}/smoky_background.jpg)`, backgroundPosition: 'left', backgroundRepeat: 'no-repeat', backgroundSize: 'cover' }}>
                {/* remove Logo here */}
                <header className="App-header w-full md:w-3/4 lg:w-2/3 xl:w-1/2 2xl:w-1/3 bg-gray-800 text-gray-200 rounded-lg shadow-2xl overflow-hidden mt-16">
                    {/* ... rest of your code */}
                </header>
                <Footer />
            </div>
        );
    }
}

export default ChatPage;
