import React from "react";
import './index.css';

class App extends React.Component {
    state = {
        input: "",
        messages: []
    };

    handleInput = event => {
        this.setState({ input: event.target.value });
    }

    handleSubmit = event => {
        event.preventDefault();
        const message = { message: this.state.input };
        this.setState(prevState => ({ messages: [...prevState.messages, message] }));
        fetch("http://localhost:8000/chat/", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(message)
        })
            .then(response => response.json())
            .then(data => this.setState(prevState => ({ messages: [...prevState.messages, { message: data.message, fromBot: true }] })));
    }

    render() {
        return (
            <div className="App h-screen bg-gray-100 flex flex-col justify-center items-center">
                <header className="App-header">
                    <div className="chat-window w-full md:w-1/2 lg:w-1/3 h-96 bg-white shadow-md rounded-lg p-6 overflow-y-auto">
                        {this.state.messages.map((message, index) =>
                            <p key={index} className={`my-2 p-2 rounded-lg ${message.fromBot ? "bg-blue-200 text-blue-800" : "bg-green-200 text-green-800"}`}>{message.message}</p>
                        )}
                    </div>
                    <form onSubmit={this.handleSubmit} className="w-full md:w-1/2 lg:w-1/3 mt-6 flex justify-between">
                        <input type="text" value={this.state.input} onChange={this.handleInput} className="chat-input flex-grow mr-2 py-2 px-4 rounded-lg border-2 border-gray-200" />
                        <button type="submit" className="chat-submit bg-blue-500 text-white py-2 px-6 rounded-lg">Submit</button>
                    </form>
                </header>
            </div>
        );
    }
}

export default App;
