import React, { useEffect } from 'react';
import { useReactMediaRecorder } from "react-media-recorder";

function ChatForm(props) {
    const {
        status,
        startRecording,
        stopRecording,
        mediaBlobUrl,
    } = useReactMediaRecorder({ audio: true });

    // Effect that runs whenever the status changes
    useEffect(() => {
        // If the recording has just been stopped
        if (status === "stopped") {
            // Send the audio to the server
            fetchAudioAndSubmit();
        }
    }, [status]); // Only re-run the effect if the status changes

    const fetchAudioAndSubmit = async (blob) => {
        const file = new File([blob], "recorded_audio.mp3", { type: "audio/mpeg" });

        // Create a FormData object
        let formData = new FormData();

        // Add the file to the form data
        formData.append("audio", file);

        // Send the audio file to the server
        fetch("http://localhost:8000/chat/audio", {
            method: "POST",
            body: formData
        })
            .then(response => response.json())
            .then(data => {
                // Assuming server responds with text corresponding to the audio
                // Inject this text into the chat input and submit the form
                props.handleInput({ target: { value: data.text } });
                props.handleSubmit();
            });
    }

    return (
        <form onSubmit={props.handleSubmit} className="border-t-2 border-gray-700 p-6 flex justify-between">
            <input type="text" value={props.input} onChange={props.handleInput} className="chat-input flex-grow mr-2 py-2 px-4 rounded-lg border-2 border-gray-700 focus:outline-none focus:border-blue-500 transition duration-150 bg-gray-700 text-gray-200" />
            <button type="submit" className="chat-submit bg-gray-800 hover:bg-gray-700 text-white py-2 px-6 rounded-lg transition duration-150">Ask</button>
            <button type="button" onClick={status === "recording" ? stopRecording : startRecording} className="chat-submit bg-gray-800 hover:bg-gray-700 text-white py-2 px-6 rounded-lg transition duration-150">{status === "recording" ? "Stop Recording" : "Record"}</button>
        </form>
    );
}

export default ChatForm;
