import React from "react";
import './index.css';

class App extends React.Component {
    state = {
        input: "",
        messages: []
    };

    handleInput = event => {
        this.setState({ input: event.target.value });
    }

    handleSubmit = event => {
        event.preventDefault();
        const message = { message: this.state.input };
        this.setState(prevState => ({ messages: [...prevState.messages, message], input: "" }));
        fetch("http://localhost:8000/chat/", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(message)
        })
            .then(response => response.json())
            .then(data => this.setState(prevState => ({ messages: [...prevState.messages, { message: data.message, fromBot: true }] })));
    }

    render() {
        return (
            <div className="App min-h-screen flex flex-col justify-center items-end pt-6 w-full pr-8" style={{ background: '#111', backgroundImage: `url(${process.env.PUBLIC_URL}/smoky_background.jpg)`, backgroundPosition: 'left', backgroundRepeat: 'no-repeat', backgroundSize: 'cover' }}>
                <div className="flex items-center justify-between w-full px-16">
                    <img src={`${process.env.PUBLIC_URL}/final_logo.png`} alt="Logo" className="h-40 w-auto" />
                    <header className="App-header w-full md:w-3/4 lg:w-2/3 xl:w-1/2 2xl:w-1/3 bg-gray-800 text-gray-200 rounded-lg shadow-2xl overflow-hidden">
                        <h1 className="text-2xl p-6" style={{ color: '#ffd700' }}>Murphy Chat</h1>
                        <div className="chat-window p-6 h-[70vh] overflow-y-auto">
                            {this.state.messages.map((message, index) =>
                                <div key={index} className={`my-2 p-2 rounded-lg ${message.fromBot ? "bg-blue-800 text-blue-300" : "bg-gray-700 text-gray-300"}`}>
                                    <p>{message.message}</p>
                                </div>
                            )}
                        </div>
                        <form onSubmit={this.handleSubmit} className="border-t-2 border-gray-700 p-6 flex justify-between">
                            <input type="text" value={this.state.input} onChange={this.handleInput} className="chat-input flex-grow mr-2 py-2 px-4 rounded-lg border-2 border-gray-700 focus:outline-none focus:border-blue-500 transition duration-150 bg-gray-700 text-gray-200" />
                            <button type="submit" className="chat-submit bg-gray-800 hover:bg-gray-700 text-white py-2 px-6 rounded-lg transition duration-150">Submit</button>
                        </form>
                    </header>
                </div>
                <footer className="text-gray-200 py-2 px-4 mt-6">
                    Â© 2023 Khatoliya & Co. | All Rights Reserved
                </footer>
            </div>
        );
    }
}

export default App;
