import React from 'react';
import './index.css';
import ChatWindow from './ChatWindow';
import ChatForm from './ChatForm';
import Footer from './Footer';
import Logo from './Logo';
import firebase from './firebase';
import { useAuthState } from 'react-firebase-hooks/auth';

class ChatPage extends React.Component {
    //... your existing code

    render() {
        const [user] = useAuthState(firebase.auth());

        const signOut = () => {
            firebase.auth().signOut();
        }

        return (
            <>
                <div className="App min-h-screen flex flex-col justify-between pt-6 w-full pr-8 pb-6" style={{ background: '#111', backgroundImage: `url(${process.env.PUBLIC_URL}/smoky_background.jpg)`, backgroundPosition: 'left', backgroundRepeat: 'no-repeat', backgroundSize: 'cover' }}>
                    <Logo />
                    {user && <button onClick={signOut} className="sign-out">Sign Out</button>}
                    // ... your existing code
                </div>
                <Footer />
            </>
        );
    }
}

export default ChatPage;
